mapper.select:
select

from car
where vim=#{vim} and id != #{carId}

select c.id as id, c.car_number as carNumber, c.car_img as carImg, c.car_model as carModel, c.car_color as carColor, c.engine_number as engineNumber, c.vim as vim, c.annual_review_time as aannualReviewTime, c.brand as brand, c.first_insurance_purchase_time as firstInsurancePurchaseTime, c.second_guarantee_mileage as secondGuaranteeMileage, c.factory_number as factoryNumber, c.owner_name as ownerName, c.owner_phone as ownerPhone, c.seat_number as seatNumber, c.purchase_time as purchaseTime, c.purchase_price as purchasePrice, c.purchase_company_id as purchaseCompanyId, c.purchase_company_name as purchaseCompanyName, c.purchase_user_id as purchaseUserId, c.purchase_user_name as purchaseUserName, c.purchase_tax as purchaseTax, c.licensing_fee as licensingFee, c.certificate_processing_time as certificateProcessingTime, c.remark as remark, c.status as status, c.create_time as createTime, c.update_time as updateTime, cs.status_name as statusName, cm.model_name as modelName from car c left join car_status cs on c.status=cs.status_id left join car_model cm on c.car_model=cm.model_id where purchase_company_id = #{companyId} and vim like CONCAT('%',#{searchKey},'%')
and status = #{carStatus}
order by c.update_time desc
